<?php namespace app\models; use Yii;use yii\base\Model;use yii\db\Query; /** * Signup form */class SignupForm extends Model{     public $username;    public $email;    public $password;    public $fio;    public $tel;     /**     * @inheritdoc     */    public function rules()    {        return [            ['username', 'safe'],            ['email', 'trim'],            ['email', 'required'],            ['email', 'email'],            ['email', 'string', 'max' => 255],            ['email', 'unique', 'targetClass' => '\app\models\User', 'message' => 'Этот адрес уже используется'],            ['password', 'required'],            ['fio', 'safe'],            ['tel', 'safe'],            ['password', 'string', 'min' => 6],        ];    }    public function attributeLabels()    {        return [            'username' => 'Имя пользователя',            'password' => 'Пароль',            'email' => 'E-mail',            'fio' => 'ФИО',            'tel' => 'Номер телефона',        ];    }     /**     * Signs user up.     *     * @return User|null the saved model or null if saving fails     */    public function signup()    {         if (!$this->validate()) {            return null;        }         $user = new User();        $user->username = $this->email;        $user->email = $this->email;        $user->fio = $this->fio;        $user->tel = $this->tel;        $user->setPassword($this->password);        $user->generateAuthKey();		$user->__set('status',3);        return $user->save() ? $user : null;    } }